import time, swisseph as swe; swe.set_ephe_path('/usr/share/ephe'); jd = swe.julday(time.localtime().tm_year, time.localtime().tm_mon, time.localtime().tm_mday); sun_info = swe.calc_ut(jd, swe.SUN)[0]; moon_info = swe.calc_ut(jd, swe.MOON)[0]; sun_pos = int((sun_info[0] / 360) * 20); moon_pos = int((moon_info[0] / 360) * 20); earth = 10; print(f"\n{' ' * sun_pos}☀️  Сонце\n{' ' * earth}🌍 Земля\n{' ' * moon_pos}🌕 Місяць\n{'_' * 0}\nПоложення Сонця: {sun_info[0]:.2f}°, Місяця: {moon_info[0]:.2f}°")